 var imp = new JavaImporter(com.vordel.trace, com.vordel.kps.impl, com.vordel.kps.query, com.vordel.kps, java.util);
 with(imp) {
	function invoke(msg) {
		var applicationId = msg.get("alert.application.id");
		var applicationName = msg.get("alert.application.name");

		var kpsTable = KPS.getInstance().getModel().getAliases().get("auth0appmap");
		var transaction = kpsTable.beginTransaction();
		try {
			var query = new KeyQuery("applicationId", applicationId);
			var iterator = transaction.query(query).iterator();
		} finally {
			transaction.close();
		}
		var extCredential;
		var clientIds = new Vector();
		while(iterator.hasNext()) {
			extCredential = iterator.next();
			//Trace.debug("Scope: "+appScope.get("uriprefix")+" enabled: " + appScope.get("enabled"));
			clientIds.add(extCredential.get("auth0ClientId"));
		}
		Trace.info("Found Auth0 Client-Ids: '" + clientIds + "'");
		msg.put("clientIds", clientIds);
		return true;
	}
 };

var imp = new JavaImporter(com.vordel.trace, java.util, com.vordel.es.xes, com.vordel.dwe, com.vordel.circuit);

with(imp) {
    function invoke(msg) {

	var clientIds = msg.get("clientIds");
	var espk = PortableESPKFactory.newInstance().createPortableESPK(
                "<key type='CircuitContainer'><id field='name' value='Identity Provider'/>"
                +     "<key type='CircuitContainer'><id field='name' value='Auth0'/>"
                +         "<key type='CircuitContainer'><id field='name' value='lib'/>"
                +             "<key type='FilterCircuit'><id field='name' value='Delete Auth0 ClientApp'/></key>"
                +         "</key>"
                +     "</key>"
                + "</key>");
	var context = new String("DefaultContext");
	var deletePolicy = Service.getInstance().getLocalPack().cache.getCircuit(espk);

	for(var i = 0; i<clientIds.size(); i++) {
		Trace.debug("Going to delete client-id: " + clientIds.get(i));
		msg.put("auth0ClientId", clientIds.get(i));
		InvocationEngine.invokeCircuit(deletePolicy, context, msg);
	}
        return true;
    }
};

var imp = new JavaImporter(com.vordel.trace, com.vordel.kps.impl, java.util);

with(imp) {
    function invoke(msg) {

	var auth0ClientId = msg.get("auth0ClientId");

	var kpsTable = KPS.getInstance().getModel().getAliases().get("auth0appmap");
	var transaction = kpsTable.beginTransaction();

	try {
		transaction.delete(auth0ClientId);
	} catch (e) {
		Trace.error("Error deleting map to Auth0-ClientId: " + auth0ClientId + "");
		Trace.error(e.getMessage());
		return false;
	} finally {
		transaction.close();
	}
	Trace.debug("Successfully deleted map of Auth0-ClientId: " + auth0ClientId + "");
        return true;
    }
};

var imp = new JavaImporter(com.vordel.trace, com.vordel.kps.impl);
with(imp) {

function invoke(msg)
{
	var auth0ClientId = msg.get("auth0ClientId");
	var externalClientId = msg.get("alert.appcredential.externalclient.id");

	// get the KPS table with alias "accessKeyTable"
	var kpsTable = KPS.getInstance().getModel().getAliases().get("PortalExternalClientStore");
	// Get the generated external ClientId row
	var extClientIdRecord = kpsTable.getCached(externalClientId);
	// Replace given ClientId with the Auth0 ClientId
	extClientIdRecord.put("clientId", auth0ClientId);

	kpsTable.update(extClientIdRecord);
	Trace.info("Successfully saved Auth0-Client-Id: " + auth0ClientId);

	return true;
}

}

var imp = new JavaImporter(com.vordel.trace, com.vordel.kps.impl, java.util);

with(imp) {
    function invoke(msg) {
	var app = msg.get("apimgmt.application");

	var appId = app.get("id");
	var appName = app.get("name");
	var auth0ClientId = msg.get("auth0ClientId");
	//var rollbackActions = msg.get("rollbackActions");

	var appMapTable = KPS.getInstance().getModel().getAliases().get("auth0appmap");

	var kpsRecord = new HashMap();
	kpsRecord.put("applicationId", appId);
	kpsRecord.put("applicationName", appName);
	kpsRecord.put("auth0ClientId", auth0ClientId);

	var transaction = appMapTable.beginTransaction();
	try {
		transaction.create(kpsRecord);	
	} catch (e) {
		Trace.error("Application-Id: " + appId + " already exists.");
		return false;
	} finally {
		transaction.close();
	}
        Trace.debug("Mapped App-Id: " + appId + " to Auth0-ClientId: " + auth0ClientId + "");
	//rollbackActions = rollbackActions + ", appClientMap";
	//msg.put("rollbackActions", rollbackActions);
        return true;
    }
};

 var base64Import = new JavaImporter(com.vordel.common.base64);
 with(base64Import) {
     function invoke(msg) {

	var token = msg.get("token");
	token = token.replace("Bearer ", "");
	msg.put("token", token);
	return true;
     }

 };
